// Debug complet des donn√©es re√ßues
console.log('üîç === DEBUG STRUCTURE API ===');
console.log('Type re√ßu:', typeof $json);
console.log('Keys principales:', Object.keys($json || {}));
console.log('Success:', $json.success);
console.log('Results pr√©sent:', !!$json.results);
console.log('Offres pr√©sentes:', !!$json.results?.offres);
console.log('Nombre offres:', $json.results?.offres?.length || 0);

if ($json.results?.offres?.length > 0) {
  console.log('‚úÖ Offres trouv√©es, aper√ßu:');
  $json.results.offres.forEach((offre, i) => {
    console.log(`  ${i+1}. "${offre.title}" par ${offre.company} √† ${offre.location}`);
  });
} else {
  console.log('‚ùå Aucune offre trouv√©e!');
}

return { json: $json };

// Pr√©paration optimis√©e des donn√©es pour Mistral
const offre = $json;
console.log('üéØ === PREPARATION MISTRAL ===');
console.log('Offre re√ßue type:', typeof offre);
console.log('Offre keys:', Object.keys(offre || {}));
console.log('Titre brut:', offre.title);
console.log('Entreprise brute:', offre.company);
console.log('Lieu brut:', offre.location);

// Extraction et nettoyage robuste
const title = String(offre.title || 'Titre non sp√©cifi√©')
  .replace(/["'\n\r\t]/g, ' ')
  .replace(/\s+/g, ' ')
  .trim()
  .substring(0, 150);

const company = String(offre.company || 'Entreprise non sp√©cifi√©e')
  .replace(/["'\n\r\t]/g, ' ')
  .replace(/\s+/g, ' ')
  .trim()
  .substring(0, 80);

const location = String(offre.location || 'Lieu non sp√©cifi√©')
  .replace(/["'\n\r\t]/g, ' ')
  .replace(/\s+/g, ' ')
  .trim()
  .substring(0, 60);

const description = String(offre.description || '')
  .replace(/["'\n\r\t]/g, ' ')
  .replace(/\s+/g, ' ')
  .trim()
  .substring(0, 200);

console.log('üìù Donn√©es nettoy√©es:');
console.log('  Titre:', title);
console.log('  Entreprise:', company);
console.log('  Lieu:', location);

// Construction du prompt pour Mistral
const prompt = `Analyse cette offre d'emploi :

Titre: ${title}
Entreprise: ${company}
Localisation: ${location}
Description: ${description}

Cette offre correspond-elle √† une alternance en cybers√©curit√© ou s√©curit√© informatique ?

R√©ponds uniquement par: VALIDE ou INVALIDE`;

console.log('ü§ñ Prompt final:', prompt.substring(0, 200) + '...');

// Construction de la requ√™te Mistral
const mistralRequest = {
  model: "mistral-small-latest",
  messages: [
    {
      role: "user",
      content: prompt
    }
  ],
  temperature: 0.1,
  max_tokens: 10
};

console.log('üì® Requ√™te Mistral pr√™te');

return {
  json: {
    mistral_request: mistralRequest,
    original_offre: offre,
    cleaned_data: {
      title: title,
      company: company,
      location: location,
      description: description
    },
    debug_info: {
      prompt_length: prompt.length,
      has_title: !!offre.title,
      has_company: !!offre.company,
      has_location: !!offre.location
    }
  }
};

// Traitement final avec gestion d'erreurs
const mistralResponse = $json;
const preparationData = $('Pr√©paration Mistral').item.json;

console.log('üéØ === TRAITEMENT RESULTAT ===');
console.log('Mistral response type:', typeof mistralResponse);
console.log('Has choices:', !!mistralResponse.choices);

let aiResponse = 'ERREUR_ANALYSE';
let isValid = false;

try {
  if (mistralResponse.choices && mistralResponse.choices[0]) {
    aiResponse = mistralResponse.choices[0].message.content || 'REPONSE_VIDE';
    const responseUpper = aiResponse.toUpperCase().trim();

    // Analyse robuste de la r√©ponse
    isValid = responseUpper === 'VALIDE' || 
             (responseUpper.includes('VALIDE') && !responseUpper.includes('INVALIDE'));

    console.log('‚úÖ R√©ponse Mistral:', aiResponse);
    console.log('‚úÖ Classification:', isValid ? 'VALIDE' : 'INVALIDE');
  } else {
    console.log('‚ùå Structure r√©ponse Mistral invalide');
    aiResponse = 'ERREUR_STRUCTURE_REPONSE';
  }
} catch (error) {
  console.log('‚ùå Erreur traitement:', error.message);
  aiResponse = 'ERREUR_EXCEPTION';
}

// R√©sultat final
const result = {
  // Donn√©es de l'offre
  title: preparationData.cleaned_data.title,
  company: preparationData.cleaned_data.company,
  location: preparationData.cleaned_data.location,
  description: preparationData.cleaned_data.description,
  url: preparationData.original_offre.url || 'URL_NON_DISPONIBLE',
  scraper_source: preparationData.original_offre.scraper_source || 'SOURCE_INCONNUE',
  search_term: preparationData.original_offre.search_term || 'TERME_INCONNU',

  // R√©sultats IA
  ai_response: aiResponse,
  is_valid: isValid,
  validation_status: isValid ? 'VALIDE' : 'INVALIDE',

  // M√©tadonn√©es
  analyzed_at: new Date().toISOString(),
  mistral_model: 'mistral-small-latest',
  debug_info: preparationData.debug_info
};

console.log('üìä R√©sultat final:', {
  title: result.title.substring(0, 30) + '...',
  company: result.company,
  status: result.validation_status
});

return { json: result };

// Rapport final avec m√©triques d√©taill√©es
const allResults = $input.all();
const processEndTime = new Date();

console.log('üìä === RAPPORT FINAL ===');
console.log('Total r√©sultats:', allResults.length);

// Classifications
const validOffers = allResults.filter(item => item.json.is_valid === true);
const invalidOffers = allResults.filter(item => item.json.is_valid === false);
const errorOffers = allResults.filter(item => 
  item.json.ai_response.includes('ERREUR') || 
  item.json.ai_response === 'REPONSE_VIDE'
);

// Statistiques par source
const sourceStats = {};
allResults.forEach(item => {
  const source = item.json.scraper_source;
  if (!sourceStats[source]) sourceStats[source] = { total: 0, valid: 0 };
  sourceStats[source].total++;
  if (item.json.is_valid) sourceStats[source].valid++;
});

// Rapport structur√©
const rapport = {
  execution_summary: {
    total_analyzed: allResults.length,
    valid_offers: validOffers.length,
    invalid_offers: invalidOffers.length,
    error_count: errorOffers.length,
    success_rate: allResults.length > 0 ? Math.round((validOffers.length / allResults.length) * 100) : 0,
    timestamp: processEndTime.toISOString()
  },

  source_breakdown: sourceStats,

  validated_offers: validOffers.map(item => ({
    title: item.json.title,
    company: item.json.company,
    location: item.json.location,
    url: item.json.url,
    source: item.json.scraper_source,
    ai_confidence: item.json.ai_response
  })),

  rejected_offers: invalidOffers.map(item => ({
    title: item.json.title,
    company: item.json.company,
    source: item.json.scraper_source,
    rejection_reason: item.json.ai_response
  })),

  processing_errors: errorOffers.map(item => ({
    title: item.json.title,
    source: item.json.scraper_source,
    error_type: item.json.ai_response
  })),

  top_locations: [...new Set(validOffers.map(item => item.json.location))].slice(0, 5)
};

console.log('‚úÖ Offres valid√©es:', rapport.execution_summary.valid_offers);
console.log('‚ùå Offres rejet√©es:', rapport.execution_summary.invalid_offers);
console.log('üî• Taux de succ√®s:', rapport.execution_summary.success_rate + '%');
console.log('üöÄ Sources utilis√©es:', Object.keys(sourceStats));

return [{ json: rapport }];
