// Retourne chaque offre comme un item s√©par√© pour traitement individuel
return offresTest.map(offre => ({ json: offre }));

// CLASSIFICATION MISTRAL - NODE CODE PRODUCTION
// Solution d√©finitive pour Docker N8N

const fetch = require('node-fetch');

// Configuration centralis√©e
const config = {
  apiKey: process.env.MISTRAL_API_KEY || 'fe8GdBIIBwYk8Dj1GvclASPE3j0Zbt95',
  apiUrl: 'https://api.mistral.ai/v1/chat/completions',
  model: 'mistral-small-latest',
  timeout: 30000
};

// Donn√©es de l'offre actuelle
const offre = $input.item.json;

console.log(`ü§ñ === CLASSIFICATION MISTRAL: ${offre.title} ===`);
console.log('üè¢ Entreprise:', offre.company);
console.log('üìç Localisation:', offre.location);

// Prompt optimis√© pour la classification
const prompt = `Analyse cette offre d'emploi :

Titre: ${offre.title}
Entreprise: ${offre.company}
Description: ${offre.description || 'Non sp√©cifi√©e'}

Cette offre correspond-elle √† une ALTERNANCE en CYBERS√âCURIT√â ?

Crit√®res :
- Doit √™tre une alternance (pas un stage ou CDI)
- Doit √™tre li√©e √† la cybers√©curit√©, s√©curit√© informatique, SOC, pentest, audit s√©curit√©

R√©ponds uniquement par: VALIDE ou INVALIDE`;

// Payload Mistral
const payload = {
  model: config.model,
  messages: [
    {
      role: "user",
      content: prompt
    }
  ],
  temperature: 0.1,
  max_tokens: 100
};

console.log('üì¶ Payload Mistral pr√©par√©');
console.log('üìè Taille prompt:', prompt.length, 'caract√®res');

try {
  // Appel API Mistral robuste
  console.log('üåê Appel API Mistral...');

  const response = await fetch(config.apiUrl, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${config.apiKey}`
    },
    body: JSON.stringify(payload),
    timeout: config.timeout
  });

  console.log('üìä Status HTTP:', response.status);

  if (!response.ok) {
    const errorText = await response.text();
    console.log('‚ùå Erreur API:', response.status, errorText);

    // Retour avec erreur mais structure pr√©serv√©e
    return {
      json: {
        ...offre,
        mistral_response: 'ERREUR_API',
        classification: 'ERREUR',
        is_valid: false,
        confidence: 0,
        error: `HTTP ${response.status}: ${errorText}`,
        processed_at: new Date().toISOString()
      }
    };
  }

  // Parse de la r√©ponse
  const data = await response.json();
  console.log('üì• R√©ponse re√ßue');

  if (data && data.choices && data.choices[0] && data.choices[0].message && data.choices[0].message.content) {
    const content = data.choices[0].message.content.trim();

    console.log('‚úÖ === MISTRAL SUCCESS ===');
    console.log('üìù R√©ponse brute:', content);

    // Classification intelligente
    const contentUpper = content.toUpperCase();
    let classification, isValid, confidence;

    if (contentUpper.includes('VALIDE') && !contentUpper.includes('INVALIDE')) {
      classification = 'VALIDE';
      isValid = true;
      confidence = 0.9;
      console.log('‚úÖ Offre VALID√âE');
    } else if (contentUpper.includes('INVALIDE')) {
      classification = 'INVALIDE';
      isValid = false;
      confidence = 0.9;
      console.log('‚ùå Offre REJET√âE');
    } else {
      // R√©ponse ambigu√´
      classification = 'INCERTAIN';
      isValid = false;
      confidence = 0.3;
      console.log('‚ö†Ô∏è R√©ponse ambigu√´');
    }

    console.log('üéØ Classification finale:', classification);
    console.log('üìä Usage tokens:', JSON.stringify(data.usage || {}));

    // Retour enrichi
    return {
      json: {
        ...offre,
        mistral_response: content,
        classification: classification,
        is_valid: isValid,
        confidence: confidence,
        model_used: data.model || config.model,
        usage: data.usage || {},
        processed_at: new Date().toISOString(),
        method: 'node_code_production'
      }
    };

  } else {
    console.log('‚ùå Structure r√©ponse Mistral invalide');
    console.log('üîç Cl√©s trouv√©es:', Object.keys(data || {}));

    return {
      json: {
        ...offre,
        mistral_response: 'STRUCTURE_INVALIDE',
        classification: 'ERREUR',
        is_valid: false,
        confidence: 0,
        error: 'Structure r√©ponse Mistral invalide',
        received_data: data,
        processed_at: new Date().toISOString()
      }
    };
  }

} catch (error) {
  console.log('üö® === ERREUR CRITIQUE ===');
  console.log('Type:', error.constructor.name);
  console.log('Message:', error.message);
  console.log('Stack:', error.stack);

  return {
    json: {
      ...offre,
      mistral_response: 'ERREUR_RESEAU',
      classification: 'ERREUR',
      is_valid: false,
      confidence: 0,
      error: `${error.constructor.name}: ${error.message}`,
      processed_at: new Date().toISOString()
    }
  };
}

// RAPPORT FINAL - Agr√©gation des r√©sultats

const allItems = $input.all();
console.log('üìä === RAPPORT FINAL CLASSIFICATION ===');
console.log('üìã Total items trait√©s:', allItems.length);

// S√©paration par statut
const validOffers = allItems.filter(item => item.json.is_valid === true);
const invalidOffers = allItems.filter(item => item.json.is_valid === false && item.json.classification !== 'ERREUR');
const errorOffers = allItems.filter(item => item.json.classification === 'ERREUR');
const uncertainOffers = allItems.filter(item => item.json.classification === 'INCERTAIN');

console.log('‚úÖ Offres valid√©es:', validOffers.length);
console.log('‚ùå Offres rejet√©es:', invalidOffers.length);
console.log('‚ö†Ô∏è Offres incertaines:', uncertainOffers.length);
console.log('üö® Offres en erreur:', errorOffers.length);

// Calcul des statistiques
const totalProcessed = allItems.length;
const successRate = Math.round((validOffers.length / totalProcessed) * 100);
const errorRate = Math.round((errorOffers.length / totalProcessed) * 100);

// Calcul usage tokens total
const totalTokens = allItems.reduce((sum, item) => {
  const usage = item.json.usage || {};
  return sum + (usage.total_tokens || 0);
}, 0);

// Rapport d√©taill√©
const rapport = {
  success: true,
  timestamp: new Date().toISOString(),
  statistics: {
    total_processed: totalProcessed,
    valid_offers: validOffers.length,
    invalid_offers: invalidOffers.length,
    uncertain_offers: uncertainOffers.length,
    error_offers: errorOffers.length,
    success_rate: `${successRate}%`,
    error_rate: `${errorRate}%`,
    total_tokens_used: totalTokens
  },
  offers: {
    validated: validOffers.map(item => ({
      title: item.json.title,
      company: item.json.company,
      location: item.json.location,
      url: item.json.url,
      mistral_response: item.json.mistral_response,
      confidence: item.json.confidence
    })),
    rejected: invalidOffers.map(item => ({
      title: item.json.title,
      company: item.json.company,
      reason: item.json.mistral_response
    })),
    uncertain: uncertainOffers.map(item => ({
      title: item.json.title,
      company: item.json.company,
      mistral_response: item.json.mistral_response
    })),
    errors: errorOffers.map(item => ({
      title: item.json.title,
      company: item.json.company,
      error: item.json.error
    }))
  },
  method: 'node_code_production',
  docker_compatible: true
};

console.log('üìà === STATISTIQUES FINALES ===');
console.log('üìä Taux de succ√®s:', rapport.statistics.success_rate);
console.log('üìä Taux d\'erreur:', rapport.statistics.error_rate);
console.log('üéØ Tokens utilis√©s:', rapport.statistics.total_tokens_used);

if (validOffers.length > 0) {
  console.log('üéâ === OFFRES VALID√âES ===');
  validOffers.forEach((item, index) => {
    console.log(`${index + 1}. ${item.json.title} - ${item.json.company}`);
  });
}

return { json: rapport };
