// Parsing de la r√©ponse IA et enrichissement des donn√©es
const offre = $input.first().json;
const aiResponse = $input.last().json.response;

// Tentative de parsing JSON de la r√©ponse IA
let aiResult;
try {
  // Extraction du JSON depuis la r√©ponse IA
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiResult = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Pas de JSON trouv√© dans la r√©ponse');
  }
} catch (error) {
  // Fallback si parsing √©choue
  aiResult = {
    is_valid: false,
    score_pertinence: 0,
    domaine: 'erreur_parsing',
    type_contrat: 'inconnu',
    raison: 'Erreur parsing r√©ponse IA',
    mots_cles: []
  };
}

// Enrichissement de l'offre avec les r√©sultats IA
const offreEnrichie = {
  ...offre,
  is_valid: aiResult.is_valid || false,
  score_pertinence: aiResult.score_pertinence || 0,
  domaine_detecte: aiResult.domaine || 'inconnu',
  type_contrat_detecte: aiResult.type_contrat || 'inconnu',
  ai_raison: aiResult.raison || '',
  ai_mots_cles: aiResult.mots_cles || [],
  ai_response: aiResponse,
  ai_processed_at: new Date().toISOString(),
  validation_status: aiResult.is_valid ? 'VALIDE' : 'INVALIDE'
};

return offreEnrichie;

// Agr√©gation de toutes les offres (valides et invalides)
const toutesLesOffres = $input.all().map(item => item.json);

// S√©paration valides/invalides
const offresValides = toutesLesOffres.filter(offre => offre.is_valid === true);
const offresInvalides = toutesLesOffres.filter(offre => offre.is_valid !== true);

// Calcul des statistiques
const stats = {
  total_offres: toutesLesOffres.length,
  offres_valides: offresValides.length,
  offres_invalides: offresInvalides.length,
  taux_validation: toutesLesOffres.length > 0 ? 
    ((offresValides.length / toutesLesOffres.length) * 100).toFixed(1) + '%' : '0%',
  score_moyen: toutesLesOffres.length > 0 ?
    (toutesLesOffres.reduce((sum, o) => sum + (o.score_pertinence || 0), 0) / toutesLesOffres.length).toFixed(1) : 0
};

// Analyse par source
const sourceStats = {};
toutesLesOffres.forEach(offre => {
  const source = offre.scraper_source || 'inconnu';
  if (!sourceStats[source]) {
    sourceStats[source] = {
      total: 0,
      valides: 0
    };
  }
  sourceStats[source].total++;
  if (offre.is_valid) {
    sourceStats[source].valides++;
  }
});

// Top entreprises
const entreprises = {};
offresValides.forEach(offre => {
  const entreprise = offre.company || 'Inconnue';
  if (!entreprises[entreprise]) {
    entreprises[entreprise] = {
      nom: entreprise,
      nb_offres: 0,
      score_moyen: 0,
      locations: new Set()
    };
  }
  entreprises[entreprise].nb_offres++;
  entreprises[entreprise].score_moyen += offre.score_pertinence || 0;
  entreprises[entreprise].locations.add(offre.location || 'Non sp√©cifi√©');
});

// Conversion Set en Array pour JSON
Object.values(entreprises).forEach(ent => {
  ent.score_moyen = ent.nb_offres > 0 ? (ent.score_moyen / ent.nb_offres).toFixed(1) : 0;
  ent.locations = Array.from(ent.locations).join(', ');
});

const topEntreprises = Object.values(entreprises)
  .sort((a, b) => b.nb_offres - a.nb_offres)
  .slice(0, 5);

// Rapport final
const rapport = {
  timestamp: new Date().toISOString(),
  execution_reussie: true,
  statistiques: stats,
  analyse_sources: sourceStats,
  top_entreprises: topEntreprises,
  toutes_offres: toutesLesOffres,
  offres_valides_uniquement: offresValides,
  resume_execution: {
    message: `Analyse termin√©e avec succ√®s ! ${stats.offres_valides} offres valides sur ${stats.total_offres} collect√©es (${stats.taux_validation})`,
    prochaine_etape: "Rapport disponible pour consultation",
    sources_actives: Object.keys(sourceStats)
  }
};

return rapport;

// G√©n√©ration d'un rapport lisible pour affichage
const data = $input.first().json;

const rapport = `
üéØ RAPPORT AGENT ALTERNANCE CYBERS√âCURIT√â
====================================================
‚è∞ Ex√©cution: ${new Date().toLocaleString('fr-FR')}

üìä STATISTIQUES GLOBALES:
‚úÖ Offres valides: ${data.statistiques.offres_valides}
‚ùå Offres invalides: ${data.statistiques.offres_invalides}
üìà Total collect√©: ${data.statistiques.total_offres}
üíØ Taux validation: ${data.statistiques.taux_validation}
‚≠ê Score moyen: ${data.statistiques.score_moyen}/100

üè¢ TOP ENTREPRISES (offres valides):
${data.top_entreprises.length > 0 ? 
  data.top_entreprises.map((ent, i) => 
    `${i+1}. ${ent.nom} - ${ent.nb_offres} offre(s) - Score: ${ent.score_moyen}/100`
  ).join('\n') : 
  'Aucune entreprise (pas d\'offres valides)'}

üìç ANALYSE PAR SOURCE:
${Object.entries(data.analyse_sources).map(([source, stats]) => 
  `${source}: ${stats.valides}/${stats.total} valides (${stats.total > 0 ? ((stats.valides/stats.total)*100).toFixed(1) : 0}%)`
).join('\n')}

üéØ OFFRES VALIDES D√âTAILL√âES:
${data.offres_valides_uniquement.length > 0 ?
  data.offres_valides_uniquement.map((offre, i) => 
    `${i+1}. ${offre.title}\n   üè¢ ${offre.company}\n   üìç ${offre.location}\n   ‚≠ê Score: ${offre.score_pertinence}/100\n   üîó ${offre.url}\n   üí≠ IA: ${offre.ai_raison}\n`
  ).join('\n') :
  'Aucune offre valide trouv√©e'}

${data.resume_execution.message}
üöÄ ${data.resume_execution.prochaine_etape}

====================================================
`;

return {
  rapport_format√©: rapport,
  data_compl√®te: data,
  status: 'success'
};
