// Traitement s√©curis√© et formatage
const originalData = $input.first()?.json || {};
const mistralResponse = $json;

console.log('üîç Traitement offre:', originalData.title);

// Extraction s√©curis√©e
let aiResponse = 'ERREUR';
try {
  if (mistralResponse?.choices?.[0]?.message?.content) {
    aiResponse = mistralResponse.choices[0].message.content.trim();
  }
} catch (error) {
  console.error('Erreur extraction:', error);
}

// Classification avec fallback
let isValid = false;
if (aiResponse.toUpperCase().includes('VALIDE')) {
  isValid = true;
} else if (aiResponse.toUpperCase().includes('INVALIDE')) {
  isValid = false;
} else {
  // Fallback: alternance dans le titre = valide
  isValid = originalData.title?.toLowerCase().includes('alternance') || false;
}

// Formatage final
const result = {
  ...originalData,
  ai_response: aiResponse,
  is_valid: isValid,
  status: isValid ? '‚úÖ VALID√âE' : '‚ùå REJET√âE',
  processed_at: new Date().toISOString()
};

console.log(`üéØ ${result.title} ‚Üí ${result.status}`);

return { json: result };

// G√©n√©ration du rapport final avec donn√©es Excel
const offers = $json.offres_validees || [];
const timestamp = new Date().toISOString().slice(0,19).replace(/[:-]/g, '');

console.log(`üìä G√©n√©ration rapport final: ${offers.length} offres`);

// Donn√©es Excel format√©es
const excelData = offers.map((offer, index) => ({
  'N¬∞': index + 1,
  'Titre': offer.title || 'N/A',
  'Entreprise': offer.company || 'N/A',
  'Localisation': offer.location || 'N/A',
  'Dur√©e': offer.duration || 'N/A',
  'D√©but': offer.start_date || 'N/A',
  'Site Source': offer.scraper_source || 'test',
  'Lien': offer.url || 'N/A',
  'Validation IA': offer.ai_response || 'VALIDE',
  'Date Traitement': new Date(offer.processed_at).toLocaleDateString('fr-FR')
}));

// Statistiques
const stats = {
  total_offres: offers.length,
  sites_scrapes: [...new Set(offers.map(o => o.scraper_source))],
  top_locations: Object.entries(
    offers.reduce((acc, o) => {
      const loc = (o.location || 'Paris').split('(')[0].trim();
      acc[loc] = (acc[loc] || 0) + 1;
      return acc;
    }, {})
  ).sort((a,b) => b[1] - a[1]),
  rapport_filename: `rapport_alternance_cybersecurite_${timestamp}.xlsx`,
  generation_date: new Date().toISOString(),
  excel_data: excelData,
  email_subject: `üéØ ${offers.length} Offres Alternance Cybers√©curit√© - ${new Date().toLocaleDateString('fr-FR')}`,
  email_summary: `‚úÖ ${offers.length} offres valid√©es par IA Mistral`
};

console.log('‚úÖ Rapport finalis√©:', stats.email_subject);

return [{ json: stats }];

// Log des rejets
