// Script de lancement des scrapers Python
const { execSync } = require('child_process');
const fs = require('fs');

const scrapers = [
  'pole_emploi',
  'indeed', 
  'apec',
  'labonnealternance',
  'walt',
  'bloom_alternance'
];

const keywords = $json.search_keywords.split(',');
const results = [];

for (const scraper of scrapers) {
  try {
    console.log(`Lancement scraper: ${scraper}`);

    // Commande Python avec paramètres
    const cmd = `python /app/scrapers/${scraper}_scraper.py --keywords="${keywords.join(' ')}" --level="${$json.target_level}" --start-date="${$json.start_date}"`;

    const output = execSync(cmd, { 
      encoding: 'utf8',
      timeout: 300000, // 5 minutes max par scraper
      maxBuffer: 10 * 1024 * 1024 // 10MB buffer
    });

    const offers = JSON.parse(output);

    for (const offer of offers) {
      offer.scraper_source = scraper;
      offer.scraped_at = new Date().toISOString();
      results.push(offer);
    }

    console.log(`${scraper}: ${offers.length} offres trouvées`);

  } catch (error) {
    console.error(`Erreur scraper ${scraper}:`, error.message);
    // Continue avec le scraper suivant
  }
}

console.log(`Total offres récupérées: ${results.length}`);

// Retourne chaque offre comme un item séparé
return results.map(offer => ({ json: offer }));

// Génération du rapport Excel avec openpyxl Python
const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

const offers = $json.offres_validees;
const timestamp = new Date().toISOString().slice(0,19).replace(/[:-]/g, '');
const filename = `rapport_alternance_cybersecurite_mistral_${timestamp}.xlsx`;
const outputPath = `/app/outputs/${filename}`;

// Créer fichier JSON temporaire avec les données
const tempDataFile = `/tmp/offers_${timestamp}.json`;
fs.writeFileSync(tempDataFile, JSON.stringify(offers, null, 2));

try {
  // Commande Python pour génération Excel
  const cmd = `python /app/excel_generator/create_report.py --input="${tempDataFile}" --output="${outputPath}" --template="cybersecurity" --ai="mistral"`;

  const result = execSync(cmd, {
    encoding: 'utf8',
    timeout: 60000
  });

  console.log('Rapport Excel généré avec Mistral:', result);

  // Statistiques
  const stats = {
    total_offres: offers.length,
    sites_scrapes: [...new Set(offers.map(o => o.site))],
    top_locations: Object.entries(
      offers.reduce((acc, o) => {
        const loc = o.localisation.split('(')[0].trim();
        acc[loc] = (acc[loc] || 0) + 1;
        return acc;
      }, {})
    ).sort((a,b) => b[1] - a[1]).slice(0,5),
    rapport_path: outputPath,
    generation_date: new Date().toISOString(),
    ai_engine: "Mistral Large"
  };

  // Nettoyage
  fs.unlinkSync(tempDataFile);

  return [{ json: stats }];

} catch (error) {
  console.error('Erreur génération rapport:', error.message);
  throw error;
}

