// G√©n√©ration du rapport Excel avec openpyxl Python
const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

const offers = $json.offres_validees;
const timestamp = new Date().toISOString().slice(0,19).replace(/[:-]/g, '');
const filename = `rapport_alternance_cybersecurite_${timestamp}.xlsx`;
const outputPath = `/app/outputs/${filename}`;

// Cr√©er fichier JSON temporaire avec les donn√©es
const tempDataFile = `/tmp/offers_${timestamp}.json`;
fs.writeFileSync(tempDataFile, JSON.stringify(offers, null, 2));

try {
  // Commande Python pour g√©n√©ration Excel
  const cmd = `python /app/excel_generator/create_report.py --input="${tempDataFile}" --output="${outputPath}" --template="cybersecurity"`;

  const result = execSync(cmd, {
    encoding: 'utf8',
    timeout: 60000
  });

  console.log('Rapport Excel g√©n√©r√©:', result);

  // Statistiques
  const stats = {
    total_offres: offers.length,
    sites_scrapes: [...new Set(offers.map(o => o.site))],
    top_locations: Object.entries(
      offers.reduce((acc, o) => {
        const loc = o.localisation.split('(')[0].trim();
        acc[loc] = (acc[loc] || 0) + 1;
        return acc;
      }, {})
    ).sort((a,b) => b[1] - a[1]).slice(0,5),
    rapport_path: outputPath,
    generation_date: new Date().toISOString()
  };

  // Nettoyage
  fs.unlinkSync(tempDataFile);

  return [{ json: stats }];

} catch (error) {
  console.error('Erreur g√©n√©ration rapport:', error.message);
  throw error;
}

