// Traitement MANUEL des offres sans splitInBatches
const apiResponse = $json;

console.log('üöÄ === TRAITEMENT MANUEL DES OFFRES ===');
console.log('API Success:', apiResponse.success);
console.log('Offres disponibles:', apiResponse.results?.offres?.length || 0);

if (!apiResponse.success || !apiResponse.results?.offres?.length) {
  console.log('‚ùå Aucune offre √† traiter');
  return [{
    json: {
      error: 'Aucune offre disponible',
      total_processed: 0,
      results: []
    }
  }];
}

// Traitement de chaque offre individuellement
const offres = apiResponse.results.offres;
const results = [];

console.log('üìä Traitement de', offres.length, 'offres...');

offres.forEach((offre, index) => {
  console.log(`\nüîç === OFFRE ${index + 1} ===`);
  console.log('Titre brut:', offre.title);
  console.log('Entreprise brute:', offre.company);
  console.log('Lieu brut:', offre.location);

  // Nettoyage des donn√©es
  const title = String(offre.title || 'Titre non sp√©cifi√©')
    .replace(/["'\n\r\t]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 150);

  const company = String(offre.company || 'Entreprise non sp√©cifi√©e')
    .replace(/["'\n\r\t]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 80);

  const location = String(offre.location || 'Lieu non sp√©cifi√©')
    .replace(/["'\n\r\t]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 60);

  const description = String(offre.description || '')
    .replace(/["'\n\r\t]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 200);

  console.log('üìù Donn√©es nettoy√©es:');
  console.log('  - Titre:', title);
  console.log('  - Entreprise:', company);
  console.log('  - Lieu:', location);

  // Cr√©ation du prompt pour Mistral
  const prompt = `Analyse cette offre d'emploi :

Titre: ${title}
Entreprise: ${company}
Localisation: ${location}
Description: ${description}

Cette offre correspond-elle √† une alternance en cybers√©curit√© ou s√©curit√© informatique ?

R√©ponds uniquement par: VALIDE ou INVALIDE`;

  // Payload Mistral
  const mistralPayload = {
    "model": "mistral-small-latest",
    "messages": [
      {
        "role": "user",
        "content": prompt
      }
    ],
    "temperature": 0.1,
    "max_tokens": 10
  };

  console.log('ü§ñ Prompt cr√©√© pour offre', index + 1);

  // Ajouter aux r√©sultats
  results.push({
    json: {
      offre_index: index + 1,
      original_data: offre,
      cleaned_data: {
        title: title,
        company: company,
        location: location,
        description: description
      },
      mistral_payload: mistralPayload,
      prompt: prompt,
      ready_for_mistral: true
    }
  });
});

console.log(`\n‚úÖ ${results.length} offres pr√©par√©es pour Mistral`);

// Retourner toutes les offres pr√©par√©es
return results;

// Traitement de la r√©ponse Mistral pour chaque offre
const mistralResponse = $json;
const offreData = $('Traitement Manuel Offres').item.json;

console.log(`üéØ === RESULTAT MISTRAL OFFRE ${offreData.offre_index} ===`);
console.log('Mistral response type:', typeof mistralResponse);
console.log('Has choices:', !!mistralResponse.choices);

let aiResponse = 'ERREUR_ANALYSE';
let isValid = false;

try {
  if (mistralResponse.choices && mistralResponse.choices[0]) {
    aiResponse = mistralResponse.choices[0].message.content || 'REPONSE_VIDE';
    const responseUpper = aiResponse.toUpperCase().trim();

    // Analyse de la r√©ponse
    isValid = responseUpper === 'VALIDE' || 
             (responseUpper.includes('VALIDE') && !responseUpper.includes('INVALIDE'));

    console.log('‚úÖ R√©ponse Mistral:', aiResponse);
    console.log('‚úÖ Classification:', isValid ? 'VALIDE' : 'INVALIDE');
  } else {
    console.log('‚ùå Structure r√©ponse Mistral invalide');
    aiResponse = 'ERREUR_STRUCTURE_REPONSE';
  }
} catch (error) {
  console.log('‚ùå Erreur traitement:', error.message);
  aiResponse = 'ERREUR_EXCEPTION';
}

// R√©sultat final pour cette offre
const result = {
  // M√©tadonn√©es
  offre_index: offreData.offre_index,
  analyzed_at: new Date().toISOString(),
  mistral_model: 'mistral-small-latest',

  // Donn√©es offre
  title: offreData.cleaned_data.title,
  company: offreData.cleaned_data.company,
  location: offreData.cleaned_data.location,
  description: offreData.cleaned_data.description,
  url: offreData.original_data.url || 'URL_NON_DISPONIBLE',
  scraper_source: offreData.original_data.scraper_source || 'SOURCE_INCONNUE',
  search_term: offreData.original_data.search_term || 'TERME_INCONNU',

  // R√©sultats IA
  ai_response: aiResponse,
  is_valid: isValid,
  validation_status: isValid ? 'VALIDE' : 'INVALIDE'
};

console.log('üìä R√©sultat offre', offreData.offre_index, ':', {
  title: result.title.substring(0, 30) + '...',
  company: result.company,
  status: result.validation_status
});

return { json: result };

// Rapport final agr√©g√©
const allResults = $input.all();

console.log('üìä === RAPPORT FINAL COMPLET ===');
console.log('Total r√©sultats trait√©s:', allResults.length);

// Classifications
const validOffers = allResults.filter(item => item.json.is_valid === true);
const invalidOffers = allResults.filter(item => item.json.is_valid === false);
const errorOffers = allResults.filter(item => 
  item.json.ai_response.includes('ERREUR') || 
  item.json.ai_response === 'REPONSE_VIDE'
);

// Statistiques par source
const sourceStats = {};
allResults.forEach(item => {
  const source = item.json.scraper_source;
  if (!sourceStats[source]) sourceStats[source] = { total: 0, valid: 0 };
  sourceStats[source].total++;
  if (item.json.is_valid) sourceStats[source].valid++;
});

// Analyse g√©ographique
const locationStats = {};
validOffers.forEach(item => {
  const location = item.json.location;
  locationStats[location] = (locationStats[location] || 0) + 1;
});

const topLocations = Object.entries(locationStats)
  .sort(([,a], [,b]) => b - a)
  .slice(0, 5)
  .map(([location, count]) => ({ location, count }));

// Rapport structur√©
const rapport = {
  execution_summary: {
    total_analyzed: allResults.length,
    valid_offers: validOffers.length,
    invalid_offers: invalidOffers.length,
    error_count: errorOffers.length,
    success_rate: allResults.length > 0 ? Math.round((validOffers.length / allResults.length) * 100) : 0,
    timestamp: new Date().toISOString(),
    processing_method: 'Manuel sans splitInBatches'
  },

  source_breakdown: sourceStats,
  geographic_analysis: topLocations,

  validated_offers: validOffers.map(item => ({
    index: item.json.offre_index,
    title: item.json.title,
    company: item.json.company,
    location: item.json.location,
    url: item.json.url,
    source: item.json.scraper_source,
    ai_confidence: item.json.ai_response
  })),

  rejected_offers: invalidOffers.map(item => ({
    index: item.json.offre_index,
    title: item.json.title,
    company: item.json.company,
    location: item.json.location,
    source: item.json.scraper_source,
    rejection_reason: item.json.ai_response
  })),

  processing_errors: errorOffers.map(item => ({
    index: item.json.offre_index,
    title: item.json.title,
    source: item.json.scraper_source,
    error_type: item.json.ai_response
  }))
};

console.log('‚úÖ Offres valid√©es:', rapport.execution_summary.valid_offers);
console.log('‚ùå Offres rejet√©es:', rapport.execution_summary.invalid_offers);
console.log('üî• Taux de succ√®s:', rapport.execution_summary.success_rate + '%');
console.log('üöÄ M√©thode:', rapport.execution_summary.processing_method);

if (validOffers.length > 0) {
  console.log('üèÜ Meilleures offres:');
  validOffers.slice(0, 3).forEach((item, i) => {
    console.log(`  ${i+1}. "${item.json.title.substring(0, 40)}..." chez ${item.json.company}`);
  });
}

return [{ json: rapport }];
