// CONFIGURATION HELLOWORK CYBERS√âCURIT√â - MULTI-REQU√äTES AVEC LIEUX
console.log('üîç === RECHERCHE HELLOWORK CYBERS√âCURIT√â (multi-combinaisons + lieux) ===');

const site_name = 'HelloWork';
const base_url = 'https://www.hellowork.com';
const keywords_main = ['cybers√©curit√©', 'reseau et telecom'];
const alternance_types = ['alternance', 'apprentissage'];
const contract_types = ['apprentissage', 'contrat-professionnalisation'];
const locations = ['Marseille', 'Paris', 'Aix-en-Provence'];
const experience_level = 'd√©butant';

const urls = [];
const configs = [];

for (const kw of keywords_main) {
  for (const alt of alternance_types) {
    for (const loc of locations) {
      const query = encodeURIComponent(`${kw} ${alt}`);
      const location_encoded = encodeURIComponent(loc);
      const url = `${base_url}/fr-fr/emploi/recherche.html?k=${query}&l=${location_encoded}&c=${contract_types.join(',')}`;
      urls.push(url);

      configs.push({
        site_name,
        base_url,
        search_params: {
          url,
          keywords: [kw, alt],
          location: loc,
          contract_types,
          experience_level
        },
        scraping_config: {
          timeout: 30000,
          retry_attempts: 3,
          user_agent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
          headers: {
            'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
            'Accept-Language': 'fr-FR,fr;q=0.9,en;q=0.8',
            'Accept-Encoding': 'gzip, deflate, br',
            'DNT': '1',
            'Connection': 'keep-alive',
            'Upgrade-Insecure-Requests': '1'
          }
        },
        expected_results: {
          min_offers: 1,
          max_offers: 20,
          target_alternance: true,
          target_cybersecurity: true
        }
      });
    }
  }
}

console.log('üéØ Site cible:', site_name);
console.log('üîó URLs g√©n√©r√©es:', urls);
console.log('üìã Nombre de requ√™tes g√©n√©r√©es:', configs.length);

// Retourne chaque configuration comme un item s√©par√© pour n8n
return configs.map(cfg => ({ json: cfg }));

// EXTRACTION OPTIMIS√âE DU CONTENEUR DES OFFRES - VERSION SPLIT COMPATIBLE
const logger = {
  info: (msg, data = {}) => console.log(`‚ÑπÔ∏è [${new Date().toISOString()}] ${msg}`, JSON.stringify(data)),
  error: (msg, error = {}) => console.error(`‚ùå [${new Date().toISOString()}] ${msg}`, JSON.stringify(error))
};

try {
  // R√©cup√©ration directe de la r√©ponse HTTP du n≈ìud Fetch Page
  const httpResponse = $input.item.json;

  // NOUVELLE M√âTHODE: R√©cup√©ration de la config depuis le n≈ìud Split Configs  
  // Maintenant que nous avons un split, chaque ex√©cution a sa propre config
  const config = $('üìã Split Configs').item.json;

  console.log('üîç Traitement de la config:', {
    site: config?.site_name,
    location: config?.search_params?.location,
    keywords: config?.search_params?.keywords,
    url: config?.search_params?.url
  });

  if (!config || !config.search_params) {
    logger.error('Config manquante depuis Split Configs', {
      hasConfig: !!config,
      configKeys: config ? Object.keys(config) : 'null'
    });
    throw new Error('Configuration invalide: search_params manquant depuis Split Configs');
  }

  logger.info('Configuration r√©cup√©r√©e avec succ√®s depuis Split', {
    site: config.site_name,
    url: config.search_params.url,
    keywords: config.search_params.keywords,
    location: config.search_params.location
  });

  const statusCode = httpResponse.statusCode || httpResponse.status;
  if (statusCode && statusCode !== 200) {
    throw new Error(`Erreur HTTP ${statusCode} pour ${config.search_params.url}`);
  }

  const htmlBody = httpResponse.body || httpResponse.data;
  if (!htmlBody) {
    throw new Error('Body HTML manquant dans la r√©ponse');
  }

  const html = htmlBody.toString();
  logger.info('HTML r√©cup√©r√©', {
    length: html.length,
    search_location: config.search_params.location,
    search_keywords: config.search_params.keywords
  });

  const selectors = [
    /<div[^>]*data-id-storage-target="list"[^>]*>([\s\S]*?)<\/div>/i,
    /<div[^>]*class="[^"]*job-list[^"]*"[^>]*>([\s\S]*?)<\/div>/gi,
    /<div[^>]*class="[^"]*offers[^"]*"[^>]*>([\s\S]*?)<\/div>/gi,
    /<main[^>]*>([\s\S]*?)<\/main>/i,
    /<section[^>]*class="[^"]*results[^"]*"[^>]*>([\s\S]*?)<\/section>/gi
  ];

  let offersHtml = null;
  let selectorUsed = -1;
  for (let i = 0; i < selectors.length; i++) {
    const match = html.match(selectors[i]);
    if (match && match[0]) {
      offersHtml = match[0];
      selectorUsed = i;
      break;
    }
  }

  if (!offersHtml) {
    logger.error('Conteneur des offres non trouv√©', {
      htmlPreview: html.substring(0, 500),
      url: config.search_params.url,
      location: config.search_params.location
    });
    throw new Error(`Conteneur des offres non trouv√© dans le HTML pour ${config.search_params.location}`);
  }

  logger.info('‚úÖ Conteneur extrait avec succ√®s', {
    selector: selectorUsed,
    length: offersHtml.length,
    location: config.search_params.location,
    keywords: config.search_params.keywords,
    url: config.search_params.url
  });

  return [{
    json: {
      offers_html: offersHtml,
      config: config,
      extraction_metadata: {
        status: 'success',
        selector_used: selectorUsed,
        html_length: offersHtml.length,
        search_location: config.search_params.location,
        search_keywords: config.search_params.keywords,
        search_url: config.search_params.url,
        extracted_at: new Date().toISOString()
      }
    }
  }];

} catch (error) {
  logger.error('Erreur extraction', {
    error: error.message,
    stack: error.stack?.substring(0, 500)
  });
  throw error;
}

// SCRAPER LISTE OFFRES OPTIMIS√â AVEC DEBUG AVANC√â
const logger = {
  info: (msg, data = {}) => console.log(`‚ÑπÔ∏è [${new Date().toISOString()}] SCRAPER: ${msg}`, JSON.stringify(data)),
  error: (msg, error = {}) => console.error(`‚ùå [${new Date().toISOString()}] SCRAPER: ${msg}`, JSON.stringify(error)),
  debug: (msg, data = {}) => console.log(`üêõ [${new Date().toISOString()}] SCRAPER: ${msg}`, JSON.stringify(data))
};

try {
  const input = $input.item.json;
  if (!input) { throw new Error('Aucune donn√©e en entr√©e'); }

  logger.debug('Structure re√ßue', {
    keys: Object.keys(input),
    hasConfig: !!input.config,
    hasOffersHtml: !!input.offers_html
  });

  const config = input.config;
  if (!config || !config.search_params) {
    logger.error('search_params manquant', { configKeys: Object.keys(config || {}) });
    throw new Error('Configuration invalide: search_params manquant');
  }

  const offers = [];
  const html = input.offers_html;

  if (!html) {
    logger.error('HTML manquant', { inputKeys: Object.keys(input) });
    throw new Error('HTML des offres manquant');
  }

  logger.info('HTML re√ßu', { length: html.length, preview: html.substring(0, 200) });

  // Essai de diff√©rents s√©lecteurs pour trouver les offres
  const selectors = [
    /<li[^>]*data-id-storage-target="item"[^>]*>([\s\S]*?)<\/li>/gi,
    /<article[^>]*class="[^"]*offer[^"]*"[^>]*>([\s\S]*?)<\/article>/gi,
    /<div[^>]*class="[^"]*job-item[^"]*"[^>]*>([\s\S]*?)<\/div>/gi,
    /<div[^>]*data-testid="offer"[^>]*>([\s\S]*?)<\/div>/gi,
    /<li[^>]*class="[^"]*offer[^"]*"[^>]*>([\s\S]*?)<\/li>/gi
  ];

  let offerItems = [];
  let usedSelector = -1;

  for (let i = 0; i < selectors.length; i++) {
    offerItems = html.match(selectors[i]) || [];
    if (offerItems.length > 0) {
      usedSelector = i;
      break;
    }
  }

  logger.info('Recherche d\'offres', {
    selectorsTest√©s: selectors.length,
    selectorUtilis√©: usedSelector,
    itemsTrouv√©s: offerItems.length
  });

  if (offerItems.length === 0) {
    // Si aucune offre trouv√©e, on examine le HTML pour comprendre la structure
    const htmlSample = html.substring(0, 2000);
    logger.error('Aucune offre trouv√©e', {
      htmlSample: htmlSample,
      possibleTags: {
        articles: (html.match(/<article/gi) || []).length,
        lis: (html.match(/<li/gi) || []).length,
        divs: (html.match(/<div/gi) || []).length
      }
    });

    // Retourner au moins une structure de base pour le debug
    return [{
      json: {
        debug_info: {
          html_length: html.length,
          html_sample: htmlSample,
          no_offers_found: true,
          config: config
        }
      }
    }];
  }

  for (let index = 0; index < offerItems.length; index++) {
    try {
      const itemHtml = offerItems[index];
      logger.debug(`Traitement offre ${index}`, {
        htmlLength: itemHtml.length,
        htmlPreview: itemHtml.substring(0, 200)
      });

      // S√©lecteurs plus flexibles
      const titleSelectors = [
        /<h3[^>]*>\s*<p[^>]*class="tw-typo-l"[^>]*>([^<]+)<\/p>/i,
        /<h3[^>]*>([^<]+)<\/h3>/i,
        /<h2[^>]*>([^<]+)<\/h2>/i,
        /<a[^>]*data-cy="offerTitle"[^>]*>([^<]+)<\/a>/i,
        /<div[^>]*class="[^"]*title[^"]*"[^>]*>([^<]+)<\/div>/i
      ];

      const companySelectors = [
        /<p[^>]*class="tw-typo-s"[^>]*>([^<]+)<\/p>/i,
        /<div[^>]*class="[^"]*company[^"]*"[^>]*>([^<]+)<\/div>/i,
        /<span[^>]*class="[^"]*company[^"]*"[^>]*>([^<]+)<\/span>/i
      ];

      let title = null, company = null, link = null;

      // Essai de chaque s√©lecteur de titre
      for (const selector of titleSelectors) {
        const match = itemHtml.match(selector);
        if (match) {
          title = match[1].trim();
          break;
        }
      }

      // Essai de chaque s√©lecteur de soci√©t√©
      for (const selector of companySelectors) {
        const match = itemHtml.match(selector);
        if (match) {
          company = match[1].trim();
          break;
        }
      }

      const linkMatch = itemHtml.match(/<a[^>]*href="([^"]+)"/i);
      link = linkMatch ? linkMatch[1] : null;

      const locationMatch = itemHtml.match(/<div[^>]*data-cy="localisationCard"[^>]*>([^<]+)<\/div>/i) ||
                           itemHtml.match(/<span[^>]*class="[^"]*location[^"]*"[^>]*>([^<]+)<\/span>/i);

      const contractMatch = itemHtml.match(/<div[^>]*data-cy="contractCard"[^>]*>([^<]+)<\/div>/i) ||
                           itemHtml.match(/<span[^>]*class="[^"]*contract[^"]*"[^>]*>([^<]+)<\/span>/i);

      const idMatch = itemHtml.match(/data-id-storage-item-id="([^"]+)"/i) ||
                     itemHtml.match(/id="([^"]+)"/i);

      const offer = {
        id: idMatch ? idMatch[1] : `offer-${index}`,
        title: title,
        company: company,
        location: locationMatch ? locationMatch[1].trim() : null,
        contract_type: contractMatch ? contractMatch[1].trim() : null,
        url: link ? (link.startsWith('http') ? link : config.base_url + link) : null,
        source_platform: config.site_name,
        scraped_at: new Date().toISOString(),
        search_metadata: {
          source_url: config.search_params.url,
          keywords: config.search_params.keywords,
          location_search: config.search_params.location
        },
        raw_html_preview: itemHtml.substring(0, 300)
      };

      logger.debug(`Offre extraite ${index}`, {
        title: offer.title,
        company: offer.company,
        hasUrl: !!offer.url
      });

      // Ajouter m√™me les offres partielles pour le debug
      if (offer.title || offer.company) {
        offers.push(offer);
      }

    } catch (error) {
      logger.error(`Erreur extraction offre ${index}`, { error: error.message });
    }
  }

  logger.info('Extraction termin√©e', { 
    totalItems: offerItems.length,
    offersExtracted: offers.length,
    sampleOffer: offers[0] || 'Aucune'
  });

  // Toujours retourner quelque chose, m√™me si vide
  if (offers.length === 0) {
    return [{
      json: {
        no_offers_extracted: true,
        debug_info: {
          items_found: offerItems.length,
          html_length: html.length,
          config: config
        }
      }
    }];
  }

  return offers.map(offer => ({ json: offer }));

} catch (error) {
  logger.error('Erreur g√©n√©rale du scraper', { error: error.message });
  // Retourner une erreur structur√©e au lieu de throw
  return [{
    json: {
      error: error.message,
      debug_info: {
        input_received: !!$input.item,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// PR√âPARATION CLASSIFICATION MISTRAL AVEC DONN√âES ENRICHIES
const offer = $input.item.json;

console.log(`ü§ñ === PR√âPARATION MISTRAL: ${offer.title} ===`);

// Construction du prompt avec toutes les donn√©es disponibles
const fullDescription = [
  offer.title,
  offer.detail?.missions || '',
  offer.detail?.profil_recherche || '',
  offer.detail?.avantages || ''
].filter(Boolean).join('\n\n');

const expertPrompt = `EXPERT CLASSIFICATION ALTERNANCE CYBERS√âCURIT√â

=== OFFRE COMPL√àTE √Ä ANALYSER ===
TITRE: ${offer.title}
ENTREPRISE: ${offer.company}
LIEU: ${offer.location}
TYPE CONTRAT: ${offer.contract_type}
DUR√âE: ${offer.duration || 'Non sp√©cifi√©e'}
SALAIRE: ${offer.salary_range || 'Non communiqu√©'}

=== DESCRIPTION COMPL√àTE ===
${fullDescription}

=== TAGS R√âSUM√â ===
${offer.detail?.resume_tags ? offer.detail.resume_tags.join(', ') : 'Aucun'}

=== CRIT√àRES VALIDATION STRICTE ===

1. üéØ TYPE CONTRAT (OBLIGATOIRE):
   ‚úÖ VALIDE: alternance, apprentissage, contrat professionnalisation
   ‚ùå INVALIDE: stage, CDI, CDD, mission, freelance

2. üõ°Ô∏è DOMAINE CYBERS√âCURIT√â/R√âSEAU (OBLIGATOIRE):
   ‚úÖ VALIDE: cybers√©curit√©, s√©curit√© informatique, SOC, SIEM, pentest, audit s√©curit√©
   ‚úÖ VALIDE: r√©seau, t√©l√©com, infrastructure, syst√®mes
   ‚ùå INVALIDE: d√©veloppement g√©n√©ral, marketing, commercial, RH

3. üìö NIVEAU FORMATION (IMPORTANT):
   ‚úÖ PR√âF√âR√â: master, bac+4, bac+5, junior, d√©butant, formation
   ‚ùå EXCLUSION: senior 5+ ans, expert, manager

4. üéì DUR√âE APPROPRI√âE:
   ‚úÖ VALIDE: 1 an, 2 ans, 12-24 mois
   ‚ùå INVALIDE: < 6 mois, > 3 ans

=== FORMAT R√âPONSE OBLIGATOIRE ===
CLASSIFICATION: VALIDE ou INVALIDE
JUSTIFICATION: [Explication pr√©cise en 1 phrase]
CONFIANCE: [0.1 √† 1.0]

Analyse cette offre maintenant.`;

const mistralPayload = {
  model: "mistral-large-latest",
  messages: [
    {
      role: "system",
      content: "Tu es un expert RH sp√©cialis√© dans l'analyse d'offres d'alternance en cybers√©curit√© et r√©seaux/t√©l√©com. Tu appliques des crit√®res stricts et √©values la pertinence pour un √©tudiant en Master cybers√©curit√©."
    },
    {
      role: "user",
      content: expertPrompt
    }
  ],
  temperature: 0.05,
  max_tokens: 300
};

console.log('üìã Payload Mistral pr√©par√©');
console.log('üìä Longueur description:', fullDescription.length);

return {
  json: {
    ...offer,
    mistral_request: {
      payload_json: JSON.stringify(mistralPayload),
      model: mistralPayload.model,
      prompt_length: expertPrompt.length,
      has_full_description: !!offer.detail?.missions
    },
    classification_prompt: expertPrompt,
    prepared_at: new Date().toISOString()
  }
};

// STOCKAGE FINAL DES OFFRES ANALYS√âES
const offer = $('üéØ Pr√©parer Mistral').item.json;
const mistralResponse = $input.item.json;

console.log(`üíæ === STOCKAGE FINAL: ${offer.title} ===`);

// Traitement de la r√©ponse Mistral
let classification = 'ERREUR';
let justification = 'Erreur traitement';
let confidence = 0;
let isValid = false;

if (mistralResponse && mistralResponse.choices && mistralResponse.choices[0]) {
  const content = mistralResponse.choices[0].message.content;

  // Parsing de la r√©ponse
  const classMatch = content.match(/CLASSIFICATION:\s*(VALIDE|INVALIDE)/i);
  const justifMatch = content.match(/JUSTIFICATION:\s*([^\n]+)/i);
  const confMatch = content.match(/CONFIANCE:\s*([0-9.]+)/i);

  classification = classMatch ? classMatch[1].toUpperCase() : 'INCERTAIN';
  justification = justifMatch ? justifMatch[1].trim() : 'Non trouv√©e';
  confidence = confMatch ? parseFloat(confMatch[1]) : 0.5;
  isValid = classification === 'VALIDE';
}

// Cr√©ation de l'objet final complet
const finalOffer = {
  // M√©tadonn√©es
  id: offer.id,
  scraped_at: offer.scraped_at,
  source_platform: offer.source_platform,
  source_url: offer.url,
  search_metadata: offer.search_metadata,

  // Donn√©es de base
  title: offer.title,
  company: offer.company,
  location: offer.location,
  contract_type: offer.contract_type,
  salary_range: offer.salary_range,
  duration: offer.duration,
  posted_date: offer.posted_date,

  // D√©tails enrichis
  detail: offer.detail || {},

  // Analyse IA
  ai_classification: {
    classification: classification,
    justification: justification,
    confidence: confidence,
    is_valid_offer: isValid,
    mistral_model: 'mistral-large-latest',
    classified_at: new Date().toISOString()
  },

  // Analyses pr√©liminaires
  preliminary_analysis: offer.preliminary_analysis,
  enriched_analysis: offer.enriched_analysis,

  // Score final
  final_score: {
    matches_criteria: isValid,
    overall_quality: confidence,
    has_full_details: !!offer.detail?.missions,
    recommended: isValid && confidence > 0.7
  }
};

// Log du r√©sultat
if (isValid) {
  console.log('üéâ ‚úÖ OFFRE VALID√âE ET STOCK√âE');
  console.log(`üè¢ ${offer.company}`);
  console.log(`üìç ${offer.location}`);
  console.log(`üìä Confiance: ${confidence}`);
} else {
  console.log(`‚ùå OFFRE REJET√âE: ${justification}`);
}

return { json: finalOffer };
